sys_script_include.script:
class EventRegistryUtil {
	constructor() {
		this.gqEventRegistry = new global.GlideQuery('sysevent_register');
	}

	insertOrUpdate(suffix, tableName, firedBy, description, priority = 100) {
		const eventName = [gs.getCurrentScopeName(), suffix].join('.');
		const sysID = this.gqEventRegistry
			.where('event_name', eventName)
			.where('table', tableName)
			.selectOne()
			.orElse({'sys_id': null})
			.sys_id;

		this.gqEventRegistry.insertOrUpdate({
			'sys_id': sysID,
			'suffix': suffix,
			'event_name': eventName,
			'table': tableName,
			'fired_by': firedBy,
			'description': description,
			'priority': priority
		});
	}
}
