sys_script_include.script:
class EventRegistryUtil {
	constructor() {
		this.gqEventRegistry = new global.GlideQuery('sysevent_register');
	}

	create = (eventName, tableName, fired_by, description, priority = 100) => { // Linter Parsing error for '=' can be safely ignored
		this.gqEventRegistry.insertOrUpdate({
			suffix: eventName,
			event_name: [gs.getCurrentScopeName(), eventName].join('.'),
			table: tableName,
			fired_by: fired_by,
			description: description,
			priority: priority
		});
	}
}
